import React from "react";
// import { useHistory } from "react-router-dom";
import * as yup from "yup";
import signSchema from '../validation/signSchema'
// import axiosWithAuth from '../components/axiosWithAuth'
import axios from "axios";

const SignUp = (props) => {


  const newUserState=props.newUserState;
  const setNewUserState=props.setNewUserState;
  const formError=props.formError;
  const setFormError=props.setFormError;
  const buttDisabled=props.buttDisabled;
  const setButtDisabled=props.setButtDisabled;
  const userPost=props.userPost;
  const setUserPost=props.setUserPost;

  //validation

  const validateChange = (e) => {
    yup.reach(signSchema, e.target.name)
      .validate(e.target.value)
      .then((valid) => {
        setFormError({
          ...formError,
          [e.target.name]: "",
        });
      })
      .catch((err) => {
        setFormError({
          ...formError,
          [e.target.name]: err.formError[0], /// this may need to say errors
        });
      });
  };

  const formSubmit = (event) => {
    event.preventDefault();
    axios
      .post(
        "https://lambda-bw-sleep-tracker.herokuapp.com/auth/signup",
        newUserState
      )
      .then((response) => {
        // localStorage.setItem('token', response.data.payload)
        // history.push('/signup')

        setUserPost(response.data);
        console.log("success", userPost);

        setNewUserState({

          username: "",
          email: "",
          first_name: "",
          last_name: "",
          password: "",

        });
      })
      .catch((err) => {
        console.log(err.response);
      });
  };

  const inputChange = (event) => {
    event.persist();
    const newFormData = {
      ...newUserState,
      [event.target.name]:
        event.target.type === "checkbox"
          ? event.target.checked
          : event.target.value,
    };
    validateChange(event);
    setNewUserState(newFormData);
  };

  return (
    <div className="logIn">
      <header>
        <h1>.Logo</h1>
        <nav>
          <ul>Home</ul>
          <ul>About</ul>
          <ul>Sign Up</ul>
        </nav>
      </header>

      <div className="logInForm">
        <h2>Sign-Up!</h2>
        <p>Track a better sleep.</p>
        <br />
        <form onSubmit={formSubmit}>
          <input
            className="username"
            name="username"
            type="text"
            placeholder="User Name"
            value={logInState.username}
            onChange={inputChange}
          />
