import React from 'react';
import * as yup from 'yup';
import formSchema from '../validation/formSchema';



const LogIn = (props) => {

    const logInState = props.logInState;
    const setLogInState = props.setLogInState;
    const errors = props.errors;
    const setErrors = props.setErrors;
    const buttonDisabled = props.buttonDisabled;



//validation
const validateChange = e => {
    yup.reach(formSchema, e.target.name)
    .validate(e.target.value)
    .then(valid => {
      setErrors({
        ...errors,[e.target.name] : ""
      });
    })
  .catch(err => {
      setErrors({
          ...errors,[e.target.name] : err.errors[0]
      });
  });
};




  return (
    <div className="logIn">


            <header>
                <h1>.logo</h1>
            </header>


        <div className="logInForm">
            <h2>Log In</h2>
            <p>Track a better sleep.</p>
            <br/>

               <input className='email' name='email' type='email' placeholder='Enter your Email'/><br/>
               <br/>
               <input className='password' name='password' type='password' placeholder='Enter your Password'/><br/>
               <br/>
               <button>Log In</button>
               <br/>
               <br/>
               <a href="./SignUp">Not a Member?<br/>
               Sign-Up to get better sleep.
               </a>
        </div>

    </div>
  );
}

export default LogIn;
