import React from "react";
// import { useHistory } from "react-router-dom";
import * as yup from "yup";
// import formSchema from "../validation/formSchema";
// import axiosWithAuth from '../components/axiosWithAuth'
import axios from "axios";
import signSchema from "../validation/signSchema";

const SignUp = (props) => {

  const users=props.users
  const setUsers=props.setUsers
  const formErrors=props.formErrors
  const setFormErrors=props.setFormErrors
  const disabled=props.disabled
  const setDisabled=props.setDisabled
  const userPost=props.userPost
  const setUserPost=props.setUserPost

  //validation

  const validateChange = (e) => {
    yup
      .reach(signSchema, e.target.name)
      .validate(e.target.value)
      .then((valid) => {
        setFormErrors({
          ...formErrors,
          [e.target.name]: "",
        });
      })
      .catch((err) => {
        setFormErrors({
          ...formErrors,
          [e.target.name]: err.formErrors[0],
        });
      });
  };

  const formSubmit = (event) => {
    event.preventDefault();
    axios
      .post(
        "https://lambda-bw-sleep-tracker.herokuapp.com/auth/signup",
        users
      )
      .then((response) => {
        // localStorage.setItem('token', response.data.payload)
        // history.push('/signup')

        setUserPost(response.data);
        console.log("success", userPost);

        setUsers({
          username: "",
          password: "",
        });
      })
      .catch((err) => {
        console.log(err.response);
      });
  };

  const inputChange = (event) => {
    event.persist();
    const newFormData = {
      ...users,
      [event.target.name]:
        event.target.type === "checkbox"
          ? event.target.checked
          : event.target.value,
    };
    validateChange(event);
    setUsers(newFormData);
  };

  return (
    <div className="signIn">
      <header>
        <h1>.Logo</h1>
        <nav>
          <ul>Home</ul>
          <ul>About</ul>
          <ul>Sign Up</ul>
        </nav>
      </header>

      <div className="signInForm">
        <h2>Zzz.</h2>
        <p>Track a better sleep.</p>
        <br />
        <form onSubmit={formSubmit}>
          <input
            className="username"
            name="username"
            type="text"
            placeholder="User Name"
            value={users.username}
            onChange={inputChange}
          />
          <br />
          {formErrors.username.length > 0 ? (
            <p className="error">{formErrors.username}</p>
          ) : null}
          <br />
          <input
            className="password"
            name="password"
            type="password"
            placeholder="Enter your Password"
            value={users.password}
            onChange={inputChange}
          />
          <br />
          {formErrors.password.length > 0 ? (
            <p className="error">{formErrors.password}</p>
          ) : null}
          <br />
          <button disabled={disabled}>Log In</button>
        </form>
        <br />
        <br />
        <a href="./SignUp">
          Not a Member?
          <br />
          Sign-Up to get better sleep.
        </a>
      </div>
      {/* <pre>{JSON.stringify(logInState, null, 2)}</pre> */}
    </div>
  );
};

export default SignUp;






























