import React, { useState, useEffect } from "react";
import axios from "axios";
// import App from "./App";

const initialFormValues = {
  username: "",
  email: "",
  fName: "",
  lName: "",
  password: "",
};

const SignUp = ({ setPost }) => {
  const [user, setUser] = useState([]);
  const [formValues, setFormValues] = useState(initialFormValues);

  const changeHandler = (evt) => {
    const { name, value } = evt.target;
    setFormValues({ ...formValues, [evt.target.name]: evt.target.value });
  };

  const submitHandler = (evt) => {
    evt.preventDefault();
    // submit();
    const newUser = {
      fName: formValues.fName.trim(),
      lName: formValues.lName.trim(),
      username: formValues.username.trim(),
      email: formValues.email.trim(),
      password: formValues.password.trim(),
    };
    setUser([...user, newUser]);
    setFormValues(initialFormValues);
  };

  return (
    <form className="formContainer" onSubmit={submitHandler}>
      <div className="App">
        <h2>Sleep better. Feel better. Live better. TODAY!</h2>
        <div className="errors">
          <div>{errors.username}</div>
          <div>{errors.email}</div>
          <div>{errors.fName}</div>
          <div>{errors.lName}</div>
          <div>{errors.password}</div>
        </div>
        <div className="logInForm">
          <h2>Sign Up!</h2>
          <p>Join Sleep Tracker for better sleep.</p>
          <br />

          <input
            className="fName"
            name="fName"
            type="fName"
            value={values.fName}
            onChange={onChange}
            placeholder="Enter your First Name"
          />
          <br />
          <br />
          <input
            className="lName"
            name="lName"
            type="lName"
            value={values.lName}
            onChange={onChange}
            placeholder="Enter your Last Name"
          />
          <br />
          <br />
          <input
            className="uName"
            name="uName"
            type="uName"
            value={values.uName}
            onChange={onChange}
            placeholder="Create your User Name"
          />
          <br />
          <br />
          <input
            className="email"
            name="email"
            type="email"
            value={values.email}
            onChange={onChange}
            placeholder="Enter your Email"
          />
          <br />
          <br />
          <input
            className="password"
            name="password"
            type="password"
            value={values.password}
            onChange={onChange}
            placeholder="Create your Password"
          />
          <br />
          <br />
          <button>Create Sleep Profile!</button>
          <br />
          <br />
          <a href="/">
            Already a Member?
            <br />
            Sign-In to get better sleep!
          </a>
        </div>
      </div>
    </form>
  );
};

export default SignUp;
